import React, { useEffect, useState } from 'react'
import Navbar from '../Navbar/Navbar'
import "./maincontainers.css"
import coverimage from "../../Images/the-conjuring-movie-franchise-house-documentary-1274867.png"
import { getAnimeList } from '../../Service/Service'

export default function Maincontainers() {
  const [popularAnime, setPopularAnime] = useState([])

  useEffect(() => {
    //setPopularAnime(getAnimeList())
    getAnimeList().then((results) => {
      setPopularAnime(results)
    })
  }, [])

  const popularAnimeList = () => {
    return popularAnime.map((anime, i) => {
      return (
        <div key={i}>
          <div>{anime.data.title}</div>
          <div>{anime.data.rating}</div>
        </div>
      )
    })
  }

  console.log({popularAnimes: popularAnime})

  return (
    <div className='mainContainer'>
      <Navbar/>
      <div className='AnimeContainer'>
        {/* <img src={`${coverimage}`} className='coverimage' alt=''/>  */}
        <div className='AnimeWrapper'>
          <div className='AnimeTittle'></div>
          <div className='AnimeTittleJpn'></div>
          <img className='AnimeImage' />
          <div className='AnimeEpisode'></div>
          <div className='AnimeDuration'></div>
          <div className='AnimeScore'></div>
        </div>
      </div>
    </div>
  )
}
